mapper.select:
SELECT DISTINCT a.ID AS uId, a.Name AS name, a.JobNum AS jobNum, c.Account_Name AS accountName, b.Name AS departname, b.ID AS departId, d.Position_Name AS positionName FROM T_Employee_Basic a INNER JOIN T_Department b ON a.Department_ID=b.ID INNER JOIN Account_Data c ON a.Account=c.Account_ID INNER JOIN Position_Data d ON c.Position_ID=d.Position_ID
AND a.Name=#{name}
AND b.Name=#{departname}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND c.Account_Name=#{accountName}
AND a.EntryDate >= #{endDate}
AND a.ResignDate<=#{startDate}
AND a.JobNum != '0'

SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, b.Name AS departname, b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName, f.Name AS classTimeName, f.Type AS classTimeType, f.ShiftDate AS shiftDate, f.StartTime AS startTime, f.EndTime As endTime
FROM T_Employee_Basic a INNER JOIN T_Department b ON a.Department_ID=b.ID INNER JOIN Account_Data c ON a.Account=c.Account_ID INNER JOIN Position_Data d ON c.Position_ID=d.Position_ID INNER JOIN T_Att_PeopleShift e ON e.UserID = a.Account INNER JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND a.Name=#{name}
AND b.Name=#{departname}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND c.Account_Name=#{accountName}
AND f.ShiftDate>=#{startDate}
AND f.ShiftDate<=#{endDate}
AND a.JobNum != '0'

SELECT DISTINCT a.UserID AS accountId, b.ShiftDate AS ctshiftDate, b.StartTime AS ctStartDate, b.EndTime As ctEndDate FROM T_Att_PeopleShift a INNER JOIN T_Att_Shift b ON a.ShiftID=b.ID
AND b.ShiftDate=#{ctEndDate} OR b.ShiftDate BETWEEN #{ctStartDate} AND #{ctEndDate}
AND a.UserID=#{accountId}

SELECT DISTINCT tb3.YYYYMMDD AS YYYYMMDD, tb3.uId AS uId, tb3.name AS name, tb3.accountName AS accountName, tb3.jobNum AS jobNum, tb3.departname AS departname, tb3.departId AS departId, tb3.accountId AS accountId, tb3.positionName AS positionName, tb4.Name AS classTimeName, tb4.Type AS classTimeType, tb4.ShiftDate AS shiftDate, tb4.StartTime AS startTime, tb4.EndTime As endTime FROM (SELECT tb1.*,tb2.* FROM ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{startDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{startDate}),#{endDate}) >=0 )tb1 , ( SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, b.Name AS departname, b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName
FROM T_Employee_Basic a left JOIN T_Department b ON a.Department_ID=b.ID left JOIN Account_Data c ON a.Account=c.Account_ID left JOIN Position_Data d ON c.Position_ID=d.Position_ID LEFT JOIN dbo.Account_Project_Data e ON c.Account_ID=e.Account_ID LEFT JOIN dbo.Project_Data f ON f.Project_ID=e.Project_ID left JOIN T_Att_PeopleShift g ON g.UserID = a.Account
AND a.Name=#{name}
AND b.Name=#{departname}
AND d.Position_Name=#{positionName}
AND a.JobNum=#{jobNum}
AND f.Project_ID=#{projectId}
)tb2 ) tb3 LEFT JOIN (SELECT i.UserID,j.EndTime,j.ShiftDate,j.StartTime,j.Name,j.Type FROM T_Att_PeopleShift i left JOIN T_Att_Shift j ON i.ShiftID=j.ID
AND j.ShiftDate >= #{startDate} AND j.ShiftDate <= #{endDate}
) tb4 ON tb3.accountId = tb4.UserID where tb4.ShiftDate is null or CONVERT(VARCHAR(100), tb4.ShiftDate, 23) = tb3.YYYYMMDD

SELECT
max(a.AttTime) AS maxAttTime,
min(a.AttTime) AS minAttTime
FROM
T_Att_Attendance a
INNER JOIN T_AttLog b ON a.AttLogID=b.ID


		AND b.Year=#{year}


		AND b.Month=#{month}


		AND b.Day=#{day}


		AND a.Userid=#{accountId}

SELECT * FROM ( ( SELECT CONVERT(CHAR(10), DATEADD(DD,NUMBER,#{startDate}),120) AS YYYYMMDD FROM MASTER..SPT_VALUES WHERE TYPE = 'P' AND DATEDIFF(MI,DATEADD(DD,NUMBER, #{startDate}),#{endDate}) >=0 )tab1, ( SELECT DISTINCT a.ID AS uId, a.Name AS name, c.Account_Name AS accountName, a.JobNum AS jobNum, b.Name AS departname, b.ID AS departId, a.Account AS accountId, d.Position_Name AS positionName, a.EntryDate AS entryDate, a.ResignDate AS resignDate
FROM T_Employee_Basic a left JOIN T_Department b ON a.Department_ID=b.ID left JOIN Account_Data c ON a.Account=c.Account_ID left JOIN Position_Data d ON c.Position_ID=d.Position_ID LEFT JOIN dbo.Account_Project_Data e ON c.Account_ID=e.Account_ID LEFT JOIN dbo.Project_Data f ON f.Project_ID=e.Project_ID left JOIN T_Att_PeopleShift g ON g.UserID = a.Account
AND a.Name=#{name}
AND b.Name=#{departname}
AND a.JobNum=#{jobNum}
AND a.JobNum is not null AND a.JobNum != '0' AND a.Account is not null AND a.Department_ID is not null AND c.Position_ID IS NOT NULL
)tab2
AND tab2.EntryDate >= #{endDate} AND tab2.ResignDate <= #{startDate}
)tab3
LEFT JOIN
( SELECT i.UserID,j.EndTime,j.ShiftDate,j.StartTime,j.Name,j.Type FROM T_Att_PeopleShift i left JOIN T_Att_Shift j ON i.ShiftID=j.ID
AND j.ShiftDate >= #{startDate} AND j.ShiftDate <= #{endDate}
)tab4 ON tab3.accountId = tab4.UserID ) where CONVERT(VARCHAR(100), tab4.ShiftDate, 23) = tab3.YYYYMMDD or tab4.ShiftDate is null order by tab3.departId

SELECT DISTINCT a.ID AS uId, b.Name AS departname, a.JobNum AS jobNum, b.ID AS departId, d.Position_Name AS positionName, f.Name AS classTimeName, f.Type AS classTimeType, f.ShiftDate AS shiftDate, f.StartTime AS startTime, f.EndTime As endTime
FROM T_Employee_Basic a INNER JOIN T_Department b ON a.Department_ID=b.ID INNER JOIN Account_Data c ON a.Account=c.Account_ID INNER JOIN Position_Data d ON c.Position_ID=d.Position_ID INNER JOIN T_Att_PeopleShift e ON e.UserID = a.ID INNER JOIN T_Att_Shift f ON e.ShiftID=f.ID
AND b.Name=#{departname}
AND f.ShiftDate>=#{startDate}
AND f.ShiftDate<=#{endDate}
AND a.JobNum != '0'
