mapper.select:
SELECT a.Account_ID AS accountId, a.Account_Name AS accountName, a.Account_Pwd AS accountPwd, a.Account_State AS accountState, a.Position_ID AS positionId, a.Account_Mac AS accountMac FROM dbo.Account_Data a
AND a.Account_ID=#{accountId}
AND a.Account_Name=#{accountName}

SELECT DISTINCT a.ID AS id, a.Account AS uAccountId, a.JobNum AS JobNum, a.Name AS name, a.Sex AS sex, d.Name AS departName, d.ID AS departId, c.Position_Name AS positionName, b.Account_Name AS ERPAaccount, b.Account_ID AS accountId, a.Birth AS birth, a.EntryDate AS entryDate, a.PositiveDate AS PositiveDate, a.ResignDate AS ResignDate, a.State AS State FROM dbo.T_Employee_Basic a INNER JOIN dbo.Account_Data b ON a.Account=b.Account_ID INNER JOIN dbo.Position_Data c ON b.Position_ID=c.Position_ID INNER JOIN dbo.T_Department d ON a.Department_ID=d.ID INNER JOIN dbo.Account_Project_Data e ON b.Account_ID=e.Account_ID INNER JOIN dbo.Project_Data f ON e.Project_ID=f.Project_ID
AND d.Name=#{departName}
AND a.State=#{state}
AND f.Project_ID=#{projectId}
ORDER BY a.ID

SELECT a.Name AS uName, a.ID AS userId, a.JobNum AS uJobNum, a.Sex AS uSex, a.Birth AS uBirth, a.Account AS uAccount, a.ID AS positionId, a.EntryDate AS uEntryDte, a.PositiveDate AS uPositiveDate, a.ResignDate AS uResignDate, a.ResignType AS uResignType, a.ResignReason AS uResignReason, a.State AS uState, a.Remark AS uRemark FROM dbo.T_Employee_Basic a
AND a.Name=#{uName}
AND a.JobNum=#{uJobNum}
AND a.ID=#{userId}
